for(var buy=document.querySelectorAll(".buy"),modalAddItem=document.querySelector(".modal-add-item"),closeAddItem=modalAddItem.querySelector(".close"),basket=document.querySelector(".basket"),i=0;i<buy.length;i++)buy[i].addEventListener("click",function(e){e.preventDefault(),modalAddItem.classList.add("modal-show"),basket.classList.add("basket-full")});closeAddItem.addEventListener("click",function(e){e.preventDefault(),modalAddItem.classList.remove("modal-show")}),window.addEventListener("keydown",function(e){27===e.keyCode&&modalAddItem.classList.contains("modal-show")&&(e.preventDefault(),modalAddItem.classList.remove("modal-show"))});