var map=document.querySelector(".map"),modalMap=document.querySelector(".modal-map"),closeMap=modalMap.querySelector(".close");map.addEventListener("click",function(e){e.preventDefault(),modalMap.classList.add("modal-show")}),closeMap.addEventListener("click",function(e){e.preventDefault(),modalMap.classList.remove("modal-show")}),window.addEventListener("keydown",function(e){27===e.keyCode&&modalMap.classList.contains("modal-show")&&(e.preventDefault(),modalMap.classList.remove("modal-show"))});